import {
  Routes,
  Route,
  useNavigationType,
  useLocation,
} from "react-router-dom";
import SplashScreen from "./pages/SplashScreen";
import Email from "./pages/Email";
import Name1 from "./pages/Name1";
import Verification from "./pages/Verification";
import LoginSignUp from "./pages/LoginSignUp";
import SignUpWithKeyboard from "./pages/SignUpWithKeyboard";
import Login from "./pages/Login";
import { useEffect } from "react";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = "";
        metaDescription = "";
        break;
      case "/email":
        title = "";
        metaDescription = "";
        break;
      case "/name":
        title = "";
        metaDescription = "";
        break;
      case "/verification":
        title = "";
        metaDescription = "";
        break;
      case "/login-sign-up":
        title = "";
        metaDescription = "";
        break;
      case "/sign-up-with-keyboard":
        title = "";
        metaDescription = "";
        break;
      case "/login":
        title = "";
        metaDescription = "";
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Routes>
      <Route path="/" element={<SplashScreen />} />
      <Route path="/email" element={<Email />} />
      <Route path="/name" element={<Name1 />} />
      <Route path="/verification" element={<Verification />} />
      <Route path="/login-sign-up" element={<LoginSignUp />} />
      <Route path="/sign-up-with-keyboard" element={<SignUpWithKeyboard />} />
      <Route path="/login" element={<Login />} />
    </Routes>
  );
}
export default App;
